import React from 'react';
import PropTypes from 'prop-types';
import Image from './Image';

class Pokemon extends React.Component {
  render () {
    const { pokemons } = this.props;
    return (
      <div className='container-de-pokemons'>
        {pokemons.map(({ id, type, averageWeight, image }) => {
        return (<div key={id} className='pokemon'>
          <div className='infor'>
            <h3>{1}</h3>
            <h4>{type}</h4>
            <h4>{`Average Weight: ${averageWeight.value} ${averageWeight.measurementUnit}`}</h4>
          </div>
          <Image source={image} altternativeText={1}/>
        </div>)
      })}
      </div>
    )
  }
}

Pokemon.propTypes = {
  pokemons: PropTypes.array.isRequired,
  id: PropTypes.number,
  name: PropTypes.string,
  type: PropTypes.string,
  averageWeight: PropTypes.object,
  image: PropTypes.string
};

export default Pokemon;
