import React from 'react';
import PropTypes from 'prop-types';
import pokemons from './data';
import Image from './Image';

class Pokemon extends React.Component {
  render () {
    const pks = pokemons;
    return (
      <div className='container-de-pokemons'>
        {pks.map((pok) => {
        return (<div key={pok.id} className='pokemon'>
          <div className='infor'>
            <h3>{pok.name}</h3>
            <h4>{pok.type}</h4>
            <h4>{`Average Weight: ${pok.averageWeight.value} ${pok.averageWeight.measurementUnit}`}</h4>
          </div>
          <Image source={pok.image} altternativeText={pok.name}/>
        </div>)
      })}
      </div>
    )
  }
}

Pokemon.propTypes = {
  pks: PropTypes.shape({
    name: PropTypes.string,
    type: PropTypes.string,
    averageWeight: PropTypes.shape({
      measurementUnit: PropTypes.string,
      value: PropTypes.number,
    }),
    image: PropTypes.string,
  }).isRequired,
};

export default Pokemon;
